import React from "react";
import requests from "../../requests";
import { Alert, Button, List, Grid, Box, TextField, Stack, ListItemText } from "@mui/material";
import FollowRequest from "../Followers/FollowRequest";
import LikeNotification from "./LikeNotification";
import CommentNotification from "./CommentNotification";
import Post from "./Post";
import DeleteIcon from '@mui/icons-material/Delete';

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Centered Modal</h4>
        <p>
          Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
          dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros.
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

class Inbox extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        currentUser: {},
        inboxList: [],
    }
}

  componentDidMount() {
      this.initializeDetails();
  }

  initializeDetails = async () => {
      try {
          // Get the author details
          const response = await requests.get('service/get-user/', {headers: {
              Authorization: localStorage.getItem('access_token'),
              accept: 'application/json',
          }});
          this.setState({ currentUser: {
              id: response.data.id ? response.data.id : '',
              url: response.data.url ? response.data.url : '',
              host: response.data.host ? response.data.host : '',
              displayName: response.data.displayName ? response.data.displayName : '',
              github: response.data.github ? response.data.github : '',
              profileImage: response.data.profileImage ? response.data.profileImage : ''
          }});

          const response_inbox = await requests.get(`service/authors/${this.state.currentUser.id}/inbox/`,
            {headers: {
              Authorization: localStorage.getItem('access_token'),
              accept: 'application/json',
              }},
              {withCredentials:true})
          this.setState({inboxList: response_inbox.data.items})
          console.log(this.state.inboxList)

      } catch(error) {
          console.log(error)
      }
  }

  clearInbox = async () => {
    try {
      const response_inbox = await requests.delete(`service/authors/${this.state.currentUser.id}/inbox/`,
            {headers: {
              Authorization: localStorage.getItem('access_token'),
              accept: 'application/json',
              }},
              {withCredentials:true})
      this.setState({inboxList: []})
    } catch(error) {
        console.log(error)
    }
  }

  renderInboxItems() {
    return this.state.inboxList.map((item) => {
        console.log('item', item)
        if (item.type === 'post') {
          return (
            <Grid item xs={8}>
              <Post author= {item.author}
              title={item.title}
              contentType={item.contentType}
              content= {item.content}
              description= {item.description}
              post= {{id: item.id}}
              currentUser={this.state.currentUser}
              />
            </Grid>
          );
        } else if (item.type === 'Follow') {
          return (
            <Grid item xs={8}>
            <FollowRequest
                displayName={item.actor.displayName}
                profileImage={item.actor.profileImage}
                currentUserId={this.state.currentUser.id}
                id={item.actor.id}
                accepted={item.accepted}
            />
            </Grid>
          );
        } else if (item.type === 'Like') {
          return (
            <Grid item xs={8}>
            <LikeNotification
              summary={item.summary}
              profileImage={item.author.profileImage}
              object={item.object}
            />
            </Grid>
          );
        } else if (item.type === 'comment') {
          return (
          <Grid item xs={8}>
            <CommentNotification
              profileImage={item.author.profileImage}
              displayName={item.author.displayName}
              owned={item.author.id === this.state.currentUser.id}
              id={item.id}
            />
            </Grid>);
        }
    });
  }

  render(){
      return (
          <div className="inbox">
            <Grid container p={2}
            justifyContent="center"
            alignItem="center"
            direction="column">
              <Button 
              variant="outlined"
              startIcon={<DeleteIcon />}
              onClick={this.clearInbox}>
                Clear inbox
              </Button>
            </Grid>
          <Grid container spacing={2} justifyContent="center" alignItem="center">
            {this.state.inboxList.length ? this.renderInboxItems() : <h2>Inbox is empty</h2>}
          </Grid>
          </div>
      )
  }
}

export default Inbox;
