import React, { useEffect, Component } from "react";
// import './App.css';
import {
    Button,
    Dialog,
    DialogTitle,
    TextField,
    DialogActions,
  } from "@mui/material";
import "../../styles/login-register.css";
import requests from "../../requests";
import { Redirect, NavLink } from "react-router-dom";

class Login extends Component {
    state = {
          username: "",
          password: ""
      }

    // state = {
    //     credentials: {username: '', password: ''}
    // }
    login = async () => {
        console.log(this.state);
        const response = await requests.post(`service/login/`, {
            username: this.state.username,
            password: this.state.password
        });
        alert(this.state);
    }

    // inputChanged = event => {
    //     const cred = this.state.credentials;
    //     cred[event.target.name] = event.target.value;
    //     this.setState({credentials: cred});
    // }

     handleChange = e => {
         alert('hi');
    this.setState({ [e.currentTarget.id]: e.currentTarget.value });
  };
  render() {
      return (
          <div className="form">
            <h1>
                Social Distributions
            </h1>
            <h3>
                Please Log in to Continue
            </h3>
            {/* <label>
                Username: <input type="text" name="username" 
                value={this.state.username}
                />
            </label> */}
            <div className="wrapper">
            <TextField
                className="text-input"
                size="small"
                type="text"
                placeholder="Username"
                value={this.state.username}
            />
            <br/>
            <TextField
                className="text-input"
                size="small"
                type="text"
                placeholder="Password"
                type="password"
                value={this.state.password}
                onChange={({ target }) =>
                this.setState({
                  password: target.value
                })
            }
            />
            <br/>
            <Button
                disabled={this.state.loginBtnDisabled}
                variant="contained"
                onClick={this.login}
                ref={node => (this.btn = node)}>
              Login
            </Button>
            </div>
            <NavLink style={{ textDecoration: 'none', position: 'relative', top: '20px' }} to="/register">Don't have an account? Sign Up</NavLink>
          </div>
        );
    }
}

export default Login;
