import React, { useEffect, Component } from "react";
// import './App.css';
import {
    Button,
    Dialog,
    DialogTitle,
    TextField,
    DialogActions,
  } from "@mui/material";
import "../../styles/login-register.css";
import requests from "../../requests";
import { Redirect, NavLink } from "react-router-dom";

class Register extends Component {
    state = {
          username: "",
          password: "",
          confirm_password: "",
      }

    state = {
        credentials: {username: '', password: ''}
    }
    login = async () => {
        console.log(this.state);
        const response = await requests.post(`service/login/`, {
            username: this.state.username,
            password: this.state.password
        });
        alert(response);
    }

  render() {
      return (
          <div className="form">
            <h1>
                Social Distributions
            </h1>
            <h3>
                Create a new account
            </h3>
            <div className="wrapper">
            <TextField
                className="text-input"
                size="small"
                type="text"
                placeholder="Username"
                value={this.state.username}
                onChange={({ target }) =>
                this.setState({
                username: target.value
                })}
            />
            <br/>
            <TextField
                className="text-input"
                size="small"
                type="text"
                type="password"
                placeholder="Password"
                value={this.state.password}
            />
            <br/>
            <TextField
                className="text-input"
                size="small"
                type="text"
                type="password"
                placeholder="Confirm Password"
                value={this.state.password}
            />
            <br/>
            <Button
                className="avonmore-button"
                disabled={this.state.loginBtnDisabled}
                color="primary"
                variant="contained"
                onClick={this.handleLogin}
                ref={node => (this.btn = node)}>
              Register
            </Button>
            </div>
            <NavLink style={{ textDecoration: 'none', position: 'relative', top: '30px' }} to="/">Have an account? Log in</NavLink>
          </div>
        );
    }
}
export default Register;
