import * as React from 'react';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import DialogTitle from '@mui/material/DialogTitle';
import Dialog from '@mui/material/Dialog';
import PersonIcon from '@mui/icons-material/Person';
import AddIcon from '@mui/icons-material/Add';
import Typography from '@mui/material/Typography';
import { blue } from '@mui/material/colors';
import DeleteIcon from '@mui/icons-material/Delete'
import EditIcon from '@mui/icons-material/Edit';
import './SimpleDialog.css';
import requests from '../../requests';

export default function SimpleDialogDemo(props) {
  const [open, setOpen] = React.useState(false);
  // const [selectedValue, setSelectedValue] = React.useState(authors[1]);
  const [comments, setComments] = React.useState(0);
  const [commenters, setCommenters] = React.useState(0);
  const [ids, setCommentIds] = React.useState(0); 

  const getComments = async () => {
    try {
      const response = await requests.get(`service/authors/${props.author_id}/posts/${props.post_id}/comments/`,
      {headers: {
      Authorization: localStorage.getItem('access_token'),
      accept: 'application/json',
      }},
      {withCredentials:true});
      console.log(response.data.items);
      var ids = [];
      var commenter_ids = [];
      response.data.items.forEach((comment) =>  {
        ids.push(comment.comment);
        commenter_ids.push(comment.author);
      });
      // comments = response.data.items;
      setComments(ids);
      setCommenters(commenter_ids);
    }
    catch(error) {
      console.log(error);
    }
  };

  React.useEffect(() => {
    getComments();
  }, []);


const handleClickOpen = async () => {
    setOpen(true);
};

  const handleClose = (value) => {
    setOpen(false);
    // setSelectedValue(value);
  };

  return (
    <div>
      <Button variant="outlined" onClick={handleClickOpen}>
        View Comments
      </Button>
      <SimpleDialog
        // selectedValue={selectedValue}
        open={open}
        onClose={handleClose}
        post_id = {props.post_id}
        comments = {comments}
        commenters = {commenters}
      />
    </div>
  );
}

function SimpleDialog(props) {

  var authors = props.commenters ? props.commenters : [];
  var comments = props.comments ? props.comments : [];
  const { onClose, selectedValue, open } = props;
  const [commentText, setCommentText] = React.useState("")

  const handleClose = () => {
    onClose(selectedValue);
  };

  const handleListItemClick = (value) => {
    alert("Create New Comment Clicked");
  };

  const handleEdit = (value) => {
    alert("Edit Clicked");
  }

  //
  const handleDelete = async (comment_id) => {
    try {
      const response = await requests.delete(`service/authors/${props.author_id}/posts/${props.post_id}/comments/${comment_id}/`,
      {headers: {
      Authorization: localStorage.getItem('access_token'),
      accept: 'application/json',
      }},
      {withCredentials:true});
    }
    catch(error) {
      console.log(error);
    }
  }

  return (
    <Dialog onClose={handleClose} maxWidth="800px" open={open}>
      <DialogTitle className="comment-dialog">Comments</DialogTitle>
      <List sx={{ pt: 0 }}>
        {authors.map((author, index) => (
          <ListItem button key={author}>
            <ListItemAvatar>
              <Avatar sx={{ bgcolor: blue[100], color: blue[600] }}>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary={`${author}: ${comments[index]}`} />
            <Button className = "edit-button" variant="contained" endIcon={<EditIcon />} onClick={handleEdit}>
              Edit 
            </Button>
            <Button className = "comment-button" color = "error" variant="contained" startIcon={<DeleteIcon />} onClick={handleDelete}>
              Delete
            </Button>
          </ListItem>
        ))}

        <ListItem autoFocus button onClick={() => handleListItemClick('addComment')}>
          <ListItemAvatar>
            <Avatar>
              <AddIcon />
            </Avatar>
          </ListItemAvatar>
          <ListItemText primary="Add Comment" />
        </ListItem>
      </List>
    </Dialog>
  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  selectedValue: PropTypes.string.isRequired,
};
