import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import './CommentDialog.css';
import requests from '../../requests';
import EditIcon from '@mui/icons-material/Edit';

export default function AddComment() {
  const [open, setOpen] = React.useState(false);
  const [comment, setComment] = React.useState('');

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSend = () => {
      alert(comment);
  }

  return (
    <div>
      <Button variant="contained" onClick={handleClickOpen} endIcon={<EditIcon />}>
        Edit
      </Button>
      <Dialog open={open} minWidth="800px" maxWidth="800px" hideBackdrop={true} onClose={handleClose}>
        <DialogTitle className="comment-dialog">Add a new comment</DialogTitle>
        <DialogContent>
          <TextField
            defaultValue="Normal"
            placeholder="I'm a placeholder"
            autoFocus
            margin="dense"
            id="name"
            label="Comment"
            type="email"
            fullWidth
            variant="standard"
            value={password}
            onChange={(event) => {setComment(event.target.value)}}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSend}>Send</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
