import React, { useEffect, Component } from "react";
// import './App.css';
import {
    Button,
    Dialog,
    DialogTitle,
    TextField,
    DialogActions,
  } from "@mui/material";
import requests from "../../requests";
import { Redirect, NavLink } from "react-router-dom";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: "",
          password: "",
        };
      }

    state = {
        credentials: {username: '', password: ''}
    }
    login = async () => {
        console.log(this.state.credentials);
        const response = await requests.post(`service/login/`, {
            username: this.state.credentials.username,
            password: this.state.credentials.password
        });
        alert(response);
    }

    inputChanged = event => {
        const cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({credentials: cred});
    }

    handleChange = e => {
        this.setState({ [e.currentTarget.id]: e.currentTarget.value });
      };
  render() {
      return (
          <div className="App">
            <h1>
              Login
            </h1>
            <label>
                Username: <input type="text" name="username" 
                value={this.state.credentials.username}
                />
            </label>
            <TextField
            className="text-input"
            size="small"
            // error={!this.state.validEmail}
            // helperText={this.state.validEmail ? "" : "Invalid Email"}
            type="text"
            placeholder="Username"
            value={this.state.credentials.username}

            // ref={input => (this.emailField = input)}

            onChange={({ target }) =>
              this.setState({
                email: target.value,
              })
            }
            // onBlur={() =>
            //   // Validate email here as it will handle the case where there is copy paste or auto-complete
            //   this.setState({
            //     validEmail: this.emailValidator()
            //   })
            // <br/>
            />
            <label>
                Password: <input type="password" name="password"
                value={this.state.credentials.password}
                onChange = {this.inputChanged}/>
            </label>
            <br/>
            <button onClick={this.login}>Login</button>
            <NavLink style={{ textDecoration: 'none', position: 'relative', top: '30px' }} to="/register">Don't have an account? Sign Up</NavLink>
          </div>
        );
    }
}

export default Login;
